sys_ux_client_script.script:
function handler({api, event, helpers, imports}) {
    var icon = {
        id: 'my-icon',
        type: 'icon',
        options: {
            icon: 'info-circle'
        }
    };

    var field1 = {
        id: 'field1',
        type: 'input',
        label: 'Field 1'
    };

    var field2 = {
        id: 'field2',
        type: 'input',
        label: 'Field 2'
    };

    var form = {
        id: 'my-form',
        type: 'form',
        options: {
            fields: [field1, field2],
            actions: []
        }
    };

    var container = {
        id: 'my-container',
        type: 'container',
        options: {
            items: [icon, form]
        }
    };

    imports.addComponent(container);
}
