sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event 
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
  const { deductions } = api.state.machine.context; 
  const { index } = api.item;
  deductions[index].dirty = true;
  api.setState('machine', {
    ...api.state.machine,
    context: {
      ...api.state.machine.context,
      deductions
    }
  });
}
