sp_angular_provider.script:
function pocButton() {
  return {
    restrict: "E",
    replace: true,
    scope: {
      type: "@",
      size: "@",
      state: "@",
      icon: "@",
    },
    template:
      "<button class=\"btn\" ng-disabled=\"state === 'disabled'\"" + 
      "  ng-class=\"{'btn-primary': type === 'primary', " + 
      "  'btn-secondary': type === 'secondary', 'btn-link': type === 'text'," + 
      "  'btn-sm': size === 'small', 'hover': state === 'hover'," + 
      "  'has-icon-left': icon === 'left', 'has-icon-right': icon === 'right'" + 
      " }\"><i ng-if=\"icon === 'left'\" class=\"glyphicon glyphicon-arrow-left\">" + 
      "</i> button <i ng-if=\"icon === 'right'\" class=\"glyphicon glyphicon-plus\">" + 
      "</i></button>",
    link: function (scope, element) {
      // implement later maybe
    },
  };
}
