sp_angular_provider.script:
function copyText() {
  return {
    restrict: 'E',
    transclude: true,
    template: '<div ng-click="copy()" style="display: flex;gap:1rem"><div ng-transclude></div>' + 
	'<i class="glyphicon glyphicon-duplicate" ng-if="!copied"></i>' + 
	'<i class="glyphicon glyphicon-ok-circle" ng-if="!!copied"></i>' + 
	'</div>',
    link: function(scope, element, attrs) {
      scope.copied = false;
      scope.copy = function () {
        var textToCopy = element.text();
		scope.copied = true;
        window.navigator.clipboard.writeText(textToCopy)
          .then(function() {
			window.setTimeout(function() {
              scope.$apply(function() {
                scope.copied = false;
              });
			}, 499); 
          })
          .catch(function(err) {
           // console.error('Failed to copy text: ', err);  

          });
      };
    }
  };
}
