sp_angular_provider.script:
function machineCrumb() {
  return {
    restrict: 'E', 
    scope: { 
      c: '=',
      exportId: '=',
      crumbs: '=',
    },
    template: '' + 
	'<ol class="breadcrumb">' + 
	'  <li class="breadcrumb-item" ng-click="c.send(\'back\')"><a href="#">Apps</a></li>' + 
	'  <li ng-if="c.matches(crumb.state)" class="breadcrumb-item" ng-repeat="crumb in crumbs">' +  
	'    <span ng-if="!crumb.active">{{crumb.label}}<span ng-if="!crumb.label">Export ID: {{exportId}}</span></span>' + 
	'    <a ng-click="c.send(\'back\')" href="#" ng-if="!!crumb.active">{{crumb.label}}<span ng-if="!crumb.label">Export ID: {{exportId}}</span></a>' + 
	'  </li>' + 
	'</ol>' 
  };
}
