sp_widget.client_script:
api.controller=function($scope) {

	// button states
	$scope.states = ['default', 'hover', 'disabled'];

	// variants
	$scope.types = ['primary', 'secondary', 'text'];

	// icon sizes
	$scope.sizes = ['large', 'small'];

	// icon placement
	$scope.icons = ['left', 'right', 'none'];

};

