  /* widget controller */
  var c = this;
  $scope.states = ["inactive", "focus", "disabled", "error"];

	$scope.valueList = ['Value', ''];
	$scope.iconPlacement = ['none', 'left', 'right'];
	$scope.helpText = ['', 'Helper text']
};

