sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event 
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    	// console.log ('eval', api.data.look_up_records_3.results);
	const prop = api.data.look_up_records_3.results
	  .filter(result =>  !api.state.excluded_users.some(user => user ===  result.user_id._reference.email.value ))
	  .map(result => `sys_created_by=${result.user_id._reference.email.value}`)
	  .join("^OR");
	// console.log ({ prop });
    api.setState('queryProp', prop)

}
