sys_ux_client_script.script:
/**
 * A function that handles an event by filtering and mapping data from an API response.
 * @param {Object} api - The API object.
 * @param {Object} event - The event object.
 * @param {Object} helpers - The helpers object.
 * @param {Object} imports - The imports object.
 */
function handleEvent({ api, event, helpers, imports }) {
  // Get the results from the API response
  const results = api.data.look_up_records_3.results;

  // Filter the results to exclude users that are in the excluded_users array
  const filteredResults = results.filter(result => !api.state.excluded_users.some(user => user === result.user_id._reference.email.value));

  // Map the filtered results to create an array of query properties
  const queryProps = filteredResults.map(result => `sys_created_by=${result.user_id._reference.email.value}`);

  // Join the query properties with "^OR" separator
  const prop = queryProps.join("^OR");

  // Set the queryProp state in the API
  api.setState('queryProp', prop);
}
