sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) { 

  return dataReduce(api.data.Get_deduction_details.result);  

  function dataReduce(obj) {
    // Create an array to hold the filtered properties
    const filteredProps = [];

    // Loop through each property in the input object
    for (const key in obj) {
      // Check if the property has a label and a value
      if (obj[key].label && obj[key].value) {
        // If the property is the "talent_user" property, extract the "enterprise_talent_id" and "full_name" properties
        if (key === "talent_user") {
          filteredProps.push({
            label: "Talent ID",
            id: obj[key].value,
            value: obj[key]._reference.enterprise_talent_id.value,
          });
          filteredProps.push({
            label: "Talent Name",
            id: obj[key].value,
            value: obj[key]._reference.full_name.value,
          });
        } else {
          // Otherwise, add the property to the filtered properties array
          filteredProps.push({
            label: obj[key].label,
            id: obj[key].value,
            value: obj[key].value,
          });
        }
      }
    }

    // Return the filtered properties array
    return filteredProps;
  }


}
