sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({ api, helpers }) {
  const { results } = api.data.get_talent;
  const emptyNode = {
    id: 'home',
    label: 'Search by Talent Name or Talent Number',
  };
  if (!results) {
  console.log ( 'NO RESULTS');
    return JSON.stringify([emptyNode]);
  }
   console.log ({ results });
  const options = results
     .filter(result => !!result.full_name.value)
     .map((result) => ({
    id: result.sys_id.value,
    label: result.full_name.value,// + ` (${result.enterprise_talent_id.value})` ,
    sublabel: `EID: ${result.enterprise_talent_id.value}, Branch: ${result.branch_name.value} Status: ${result.status_crm.value}`,
    talent_id: result.enterprise_talent_id.value,
    email: result.primary_email.value,
    sys_id: result.sys_id.value
  }));
  // console.log ({ options });
  return JSON.stringify([emptyNode, ...options]);
}
