sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  const { results } = api.data.look_up_records_3;
  const branchOpts = api.data.look_up_records_2.results.map(result => ({
    id: result.branch_id.value,
    label: `Any user in "${result.branch_id.displayValue}"`,
    icon: !api.state.excluded_users.length ? 'check-fill' : null
  }));

	const userOpts = results.map(result => ({
    id: result.user_id.value,
    label: result.user_id.displayValue,
    email: result.user_id._reference.email.value,
    icon: api.state.excluded_users.some(user => user === result.user_id._reference.email.value)
       ? null
       : "check-fill"
  }));
  return [...branchOpts, ...userOpts];

}
