sys_ux_client_script.script:
// Page scripts - Clear state
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const defaultState = {
        "form_sys_id": -1,
        "deductions": [],
        "deduction_notes": null,
        "deduction_eff_date": null,
        "isEffectiveDateCalendarVisible": false,
        "search_user_input": "",
        "user_selected": null,
        "isSearchVisible": false,
        "forceUpdate": null,
        "created_sys_id": null,
        "submitted": false
    };

    for (let prop in defaultState) {
        api.setState(prop, defaultState[prop]);
    }


}
